package p001;

public class Programacion4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//funciones
		int i=0;
		System.out.println("El valor de i es: "+i);
		saludar();
		i++;
		System.out.println("El valor de i es: "+i);

	}

	
	public static void saludar() {
		for (int i=0; i != 10; i++) {
			System.out.println("Saludos");
		}
		
	}
	
}
